
const productCardSlider = (elements) =>{

  const elementsSlide = document.querySelectorAll(elements);

  elementsSlide.forEach( (element) => {
    const durationValue = element.getAttribute('duration') * 1000 ?? 5000;
    const productPerView = element.getAttribute('products-per-view') ?? 1
    const isLoop = element.getAttribute('isLoop') ?? true
    const speed = element.getAttribute('speed') ?? 500

    const swiper = new Swiper(element, {
      autoplay: {
        delay: durationValue
      },
      speed,
      loop: isLoop,
      slidesPerView: productPerView
    });
  });

}

    
productCardSlider('.swiper.mega_menu-collection')
productCardSlider('.swiper.annountcement-bar')
