var swiperElements = document.querySelectorAll('.swiper.annountcement-bar');

swiperElements.forEach(function (element) {
  var durationValue = element.getAttribute('duration') * 1000;

  // Tạo Swiper cho từng phần tử
  var swiper = new Swiper(element, {
    autoplay: {
      delay: durationValue,
    },
    speed: 500,
    spaceBetween: 0,
    loop:true,
  });

});
